const db = require("../db")

const getAllCourse = async(req,res,next)=>{   
    try {
        const course_name = req.query.course_name
         const search = `select * from course where course_name like '%${course_name}%' LIMIT 5`
         console.log("search", search);
         db.query(search,(err,result)=>{
            console.log(result,"result")
            
            if(err){
               return res.status(500).send("course not found")
              }else{
                return res.json({
                    status:true,
                    data:result,
                    message:"this is a course"   
                })
            }}
            )} catch (error) {
        console.log(error);
    }}



// 
// 

module.exports = {getAllCourse}